mapper.select:
	select *
	from ( select rownum rn, a.* 
	       from ( select * 
	              from board 
	              where b_type = #{b_type} 
	              order by b_idx desc
                 ) a
          )
    where rn between #{start} and #{end}

mapper.update:
update board
set b_title = #{b_title},

	b_upload = #{b_upload},

b_content = #{b_content}
where b_idx = #{b_idx}

mapper.delete:
{call
     declare
     begin
         delete from board    where b_idx = #{b_idx} ;
         delete from comment1 where b_idx = #{b_idx} ;
     end
 }
